import React, { useState } from 'react';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import { fade, withStyles, } from '@material-ui/core/styles';
import InputBase from '@material-ui/core/InputBase';

export default function ContactWay(props) {

  // The way to contact
  const [contactWay, setContactWay] = useState(props.contact);
  const BootstrapInput = withStyles((theme) => ({
    root: {
      'label + &': {
        marginTop: theme.spacing(3),
      },
    },
    input: {
      borderRadius: 4,
      position: 'relative',
      backgroundColor: theme.palette.common.white,
      border: '1px solid #ced4da',
      fontSize: 16,
      width: 'auto',
      padding: '10px 12px',
      transition: theme.transitions.create(['border-color', 'box-shadow']),
      // Use the system font instead of the default Roboto font.
      fontFamily: [
        '-apple-system',
        'BlinkMacSystemFont',
        '"Segoe UI"',
        'Roboto',
        '"Helvetica Neue"',
        'Arial',
        'sans-serif',
        '"Apple Color Emoji"',
        '"Segoe UI Emoji"',
        '"Segoe UI Symbol"',
      ].join(','),
      '&:focus': {
        boxShadow: `${fade(theme.palette.primary.main, 0.25)} 0 0 0 0.2rem`,
        borderColor: theme.palette.primary.main,
      },
    },
  }))(InputBase);

  const onContactWayChange = (e) => {
    setContactWay(e.target.value);
    props.onChange(e.target.value)
  }

  return (
    <div>
      <FormControl>
        <InputLabel shrink htmlFor="contact-way">
          請留下聯絡方式:
        </InputLabel>
        <BootstrapInput 
          placeholder={contactWay} 
          onChange={onContactWayChange} 
          id="contact-way" 
        />
      </FormControl>
    </div>
  )
}
